create table banco (id bigint generated by default as identity, icone_url varchar(255), nome varchar(255), primary key (id));
create table categoria (ativa boolean default true not null, id bigint generated by default as identity, cor varchar(255) not null, nome varchar(255) not null unique, tipo_categoria varchar(255) check (tipo_categoria in ('DESPESA','RECEITA')), primary key (id));
create table conta (saldo numeric(38,2), banco_id bigint, id bigint generated by default as identity, tipo_conta_id bigint, usuario_id bigint, primary key (id));
create table tipo_conta (id bigint generated by default as identity, tipo_conta varchar(255) check (tipo_conta in ('CONTA_CORRENTE','CARTEIRA','POUPANCA','INVESTIMENTOS','OUTRO')), primary key (id));
create table transacao (valor numeric(38,2) not null, categoria bigint not null, conta_origem bigint, data_transacao DATE, id bigint generated by default as identity, comentario TEXT, descricao TEXT not null, tipo varchar(255) check (tipo in ('RECEITA','DESPESA')), primary key (id));
create table usuario (id bigint generated by default as identity, email varchar(255) not null unique, nome varchar(255) not null, senha varchar(255) not null, primary key (id));
alter table if exists conta add constraint FKk8bnbh61n1vif9wv74uh8je2w foreign key (banco_id) references banco;
alter table if exists conta add constraint FKc6nuq2fjxv7g72nq5284mry9s foreign key (tipo_conta_id) references tipo_conta;
alter table if exists conta add constraint FKmn9bk6wpmnlqyv5i3utmled4c foreign key (usuario_id) references usuario;
alter table if exists transacao add constraint FKojgvwa1yxa7se9wqo8ed6lcb1 foreign key (categoria) references categoria;
alter table if exists transacao add constraint FKknuq5yi1h2sjspj0hd82s6m90 foreign key (conta_origem) references conta;
